// Non-negative-valued modulo, by jthill:
//   http://stackoverflow.com/questions/14997165/fastest-way-to-get-a-positive-modulo-in-c-c?lq=1
//
// Like the remainder (%) operator but returns a nonnegative modulus 
//  even when i is negative.
inline int positive_mod(int i, int n)
{
    const int shift = CHAR_BIT*(sizeof i) - 1;
    int m = i%n;
    return m + (m>>shift & n);
}
  
inline bool mxIsScalar(const mxArray *a)
{
    return mxGetM(a)*mxGetN(a) == 1;
}
  
#define min(a,b) (((a) < (b)) ? (a) : (b));
#define max(a,b) (((a) > (b)) ? (a) : (b));

template<class T>
        inline T EdgeCutoff(T x)
{
    if (x <= -Fuzziness) return 0;
    if (x >= Fuzziness) return 1;
    double d = 0.5*(x/Fuzziness + 1);
    return EdgeFilter[lround(EdgeGran*d)];
}

// Approximate the integral of the filter over a wedge shaped region
//  with center at distance d from the filter's center, starting from the
//  line through the filter center and the wedge center and with an 
//  internal angle arccos(dp).
template<class T>
        inline T CornerCutoff(T d, T dp)
{
    T a;
    
    d *= (1/Fuzziness);
    if (d <= 0) d = 0;
    else if (d >= 1) d = 1;
    
    if (dp <= -1) a = 1;
    else if (dp >= 1) a = 0;
    else a = acos(dp)/pi;
    
    return CornerFilter[lround(CornerGranD*d)][lround(CornerGranAngle*a)];
}

template<class T>
        void RenderTri(T *image, const T ax1[], const T ax2[], int i0, int i1, int i2, T value, int m, int n, bool periodic)
{
    T no[3][2], n1, n2, s, dp[3], x1[3], x2[3], c0, c1, c2, nd, mask;
    int minx1=m, minx2=n, maxx1=1, maxx2=1;
    int i, j, k;
    bool orientation;   // Orientation: false for clockwise orientation,
                        //              true for counterclockwise.
    
    // Round up the coordinates of the vertices.
    x1[0] = ax1[i0];    x2[0] = ax2[i0];
    x1[1] = ax1[i1];    x2[1] = ax2[i1];
    x1[2] = ax1[i2];    x2[2] = ax2[i2];
    
    // Take cross product between 0-1 edge and 0-2 edge.
    nd = (x1[1]-x1[0])*(x2[2]-x2[0]) - (x2[1]-x2[0])*(x1[2]-x1[0]);
    
    // Check for (nearly) collinear vertices and bail in this case.
    if (nd > -CollinearThreshold && nd < CollinearThreshold) {
        mexDebugPrintf("Nearly collinear vertices: bailing\n");
        return;
    }
    // Record orientation for posterity.
    orientation = nd > 0;
    
    mexDebugPrintf("Rendering triangle (%f,%f) - (%f,%f) - (%f,%f)\n", x1[0], x2[0], x1[1], x2[1], x1[2], x2[2]);
    
    for (i=0;i<=2;i++) {
        n1 = -(x2[(i+1)%3]-x2[i]);
        n2 = x1[(i+1)%3]-x1[i];
        s = sqrt(n1*n1+n2*n2);
        if (s <= CoincidenceThreshold) {
            // Two vertices in the same location: degenerate case.
            mexDebugPrintf("Coincident vertices: bailing\n");
            return;
        }
        s = 1/s;
        n1 *= s;
        n2 *= s;
        minx1 = min(minx1, (int) ceil(x1[i]-Fuzziness));
        minx2 = min(minx2, (int) ceil(x2[i]-Fuzziness));
        maxx1 = max(maxx1, (int) floor(x1[i]+Fuzziness));
        maxx2 = max(maxx2, (int) floor(x2[i]+Fuzziness));
        
        // Take the dot product of vertex i with the normal for the
        //  edge between it and vertex i + 1.
        dp[i] = n1*x1[i] + n2*x2[i];
        
        // Make sure the normal points toward the vertex not on this edge.
        // If not, flip the normal (and dot product)
        nd = x1[(i+2)%3]*n1 + x2[(i+2)%3]*n2 - dp[i];
        if (nd < 0) {
            mexDebugPrintf("Flipping normal\n");
            n1 = -n1; n2 = -n2; dp[i] = -dp[i];
        } else if (nd > -CollinearThreshold && nd < CollinearThreshold) {
            // The vertices are (nearly) collinear; bail
            mexDebugPrintf("Nearly collinear vertices: bailing\n");
            return;
        }
        
        no[i][0] = n1;
        no[i][1] = n2;
    }
    
	if (!periodic) {
		minx1 = max(minx1,1);   minx2 = max(minx2,1);
		maxx1 = min(maxx1,m);   maxx2 = min(maxx2,n);
    }
	
    mexDebugPrintf("Bounding box: (%d-%d,%d-%d)\n", minx1, maxx1, minx2, maxx2);
    mexDebugPrintf("Normals: (%f,%f), (%f,%f), (%f,%f)\n", no[0][0], no[0][1], no[1][0], no[1][1], no[2][0], no[2][1]);
    
    
    for (i=minx1; i<=maxx1; i++) {
        for (j=minx2; j<=maxx2; j++) {
            c0 = EdgeCutoff(i*no[0][0] + j*no[0][1] - dp[0]);
            c1 = EdgeCutoff(i*no[1][0] + j*no[1][1] - dp[1]);
            c2 = EdgeCutoff(i*no[2][0] + j*no[2][1] - dp[2]);
            if (c0 == 0 || c1 == 0 || c2 == 0) continue; // pixel outside triangle
            mask = c0 + c1 + c2 - 2;
            if (((c0<1.) + (c1<1.) + (c2<1.)) > 1) {
                bool inRange = false;
                
                mexDebugPrintf("Pixel (%d,%d) needs corner work, mask = %f\n", i, j, mask);
            
                // Next to 2 or more edges: need to compute
                //  corner contributions and add them back in
                //  after subtracting edge contributions...
                
                for (k=0;k<3;k++) {
                    T d1, d2, dL, dLi, cm, dp;
                    
                    d1 = x1[k]-i;
                    d2 = x2[k]-j;
                    dL = d1*d1+d2*d2;
                    
                    if (dL > Fuzziness*Fuzziness) {
                        // Corner is too far away from pixel and won't
                        //  contribute.
                        mexDebugPrintf("  Corner %d is (%f,%f) from pixel center; too far\n", k, d1, d2);
                        continue;
                    } else if (dL < CenterThreshold*CenterThreshold) {
                        // Vertex is basically at pixel center.
                        // Use arccos to get the angle, and divide by 2*pi.
                        mexDebugPrintf("  Vertex %d nearly at pixel center.\n", k);
                        dp = -no[k][0]*no[(k+2)%3][0] - no[k][1]*no[(k+2)%3][1];
                        mexDebugPrintf("  Dot product = %f\n", dp);
                        
                        if (dp > 1) dp = 1;
                        else if (dp < -1) dp = -1;
                        
                        cm = acos(dp) / 6.283185307179586;
                        inRange = true;
                    } else {
                        T tp1, tp2, tn1, tn2, epdp, endp, epcp, encp;
                        T cmp, cmn;
                        
                        // Compute normalized dot products of
                        //  d with edge tangent vectors.
                        dL = sqrt(dL);
                        dLi = 1 / dL;
                        
                        mexDebugPrintf("  Corner %d is (%f,%f) from pixel center\n", k, d1, d2);
                        
                        d1 *= dLi; d2 *= dLi;
                        tp1 = -no[(k+2)%3][1];
                        tp2 = +no[(k+2)%3][0];
                        tn1 = +no[k][1];
                        tn2 = -no[k][0];
                        
                        epdp = tp1*d1 + tp2*d2;
                        endp = tn1*d1 + tn2*d2;
                        epcp = tp1*d2 - tp2*d1;
                        encp = tn1*d2 - tn2*d1;
                        if (orientation) {
                            epdp = -epdp; endp = -endp;
                            // Should flip cross products (epcp, encp)
                            //  but it won't affect us...
                        }
                        cmp = CornerCutoff(dL, epdp);
                        cmn = CornerCutoff(dL, endp);
                        
                        mexDebugPrintf("  Dot products: %f, %f; corner contributions: %f, %f\n", epdp, endp, cmp, cmn);
                        
                        if (epcp*encp > 0) {
                            cm = fabs(cmp - cmn);
                        } else if (epdp+endp > 0) {
                            cm = cmp + cmn;
                        } else if (epdp+endp < 0) {
                            cm = 1 - (cmp + cmn);
                        } else {
                            // Should not happen; we should have
                            //  already weeded out degenerate
                            //  triangles.
                            cm = 0;
                            mexDebugPrintf("  Unexpectedly encountered degenerate triangle\n");
                        }
                        inRange = true;
                    }
                    
                    mexDebugPrintf("  Adding %f back in for corner %d\n", cm, k);
                    mask += cm;
                }
                
                // If none of the corners were close enough, and the mask
                //  is negative, then this pixel is outside the triangle.
                // Skip to the next!
                if (!inRange && mask < 0) continue;
            }

            // Reduce indices modulo grid size (for the periodic case)
            unsigned int im = positive_mod(i - 1, m);
            unsigned int jm = positive_mod(j - 1, n);
            
            image[jm*m+im] += value * mask;
        }
    }
}


template<class T>
        void RenderQuad(T *image, const T x1[4], const T x2[4], T value, int m, int n, bool periodic)
{
    T n11, n12, n21, n22, dp, dp1, dp2, d, det, n1, n2, p1, p2;
    int i, j;
    bool side[2];
    int straddles = 0;
    
    for (i=0;i<=3;i++) {
        if (isnan(x1[i]) || isnan(x2[i])) {
            // If there is a NaN vertex: draw a triangle instead.
            mexDebugPrintf("NaN vertex (%d): triangle\n", i);
            RenderTri(image, x1, x2, (i+1)%4, (i+2)%4, (i+3)%4, value, m, n, periodic);
            return;
        }

        n1 = -(x2[(i+1)%4]-x2[i]);
        n2 = x1[(i+1)%4]-x1[i];
        if ((n1*n1+n2*n2) < CoincidenceThreshold*CoincidenceThreshold) {
            // Two vertices in the same location: it's a triangle.
            mexDebugPrintf("Duplicate vertices (%d,%d): triangle\n", i, (i+1)%4);
            RenderTri(image, x1, x2, (i+1)%4, (i+2)%4, (i+3)%4, value, m, n, periodic);
            return;
        }
        
        // Take the dot product of vertex i with the normal for the
        //  edge between it and vertex i + 1.
        dp = n1*x1[i] + n2*x2[i];
        
        // Check which side of the edge the two vertices not on this edge
        //  are on.
        side[0] = (x1[(i+2)%4]*n1 + x2[(i+2)%4]*n2) > dp;
        side[1] = (x1[(i+3)%4]*n1 + x2[(i+3)%4]*n2) > dp;
        
        // Vertices on opposite sides: nonconvex or twisted quadrilateral.
        // If there are edges like this, there should be exactly two of
        //  them.
        // Record these edges in a bitmask.
        straddles |= (side[0] != side[1]) << i;
    }
    
    mexDebugPrintf("Straddle mask: %d\n", straddles);
    
    // Now split into triangles.
    switch (straddles) {
        case 0:
            // Convex quadrilateral: can split any way
            
            // Fall through to case below
        case 3:
        case 12:
            // Nonconvex: split on the v1-v3 edge
            RenderTri(image, x1, x2, 0, 1, 3, value, m, n, periodic);
            RenderTri(image, x1, x2, 2, 1, 3, value, m, n, periodic);
            break;
        case 6:
        case 9:
            // Nonconvex: split on the v0-v2 edge
            RenderTri(image, x1, x2, 1, 0, 2, value, m, n, periodic);
            RenderTri(image, x1, x2, 3, 0, 2, value, m, n, periodic);
            break;
        case 5:
        case 10:
            int a0, a1, b0, b1;
            // Twisted: find intersection point. This case shouldn't happen
            //  very often so it's not super time-critical.
            
            // Get the vertex numbers a0,a1,b0,b1 of the two twisted edges,
            //  a and b.
            if (straddles == 5) {
                a0 = 0; a1 = 1; b0 = 2; b1 = 3;
            } else {
                a0 = 1; a1 = 2; b0 = 3; b1 = 0;
            }

            // Get normals to the two twisted edges and assemble into
            //  a matrix.
            n11 = -(x2[a1] - x2[a0]);  n12 = x1[a1] - x1[a0];
            n21 = -(x2[b1] - x2[b0]);  n22 = x1[b1] - x1[b0];
            
            // v1 is always in the first twisted edge; v3 is always in
            //  the second; use v1 and v3 to take dot products with the
            //  the normals.
            dp1 = x1[1]*n11 + x2[1]*n12;
            dp2 = x1[3]*n21 + x2[3]*n22;

            mexDebugPrintf("Matrix:\n%f %f\n%f %f\n", n11, n12, n21, n22);

            // Get and check the determinant.
            det = n11*n22 - n12*n21;
            if (det > -DetThreshold && det < DetThreshold) {
                // The two twisted edges are nearly parallel; don't worry
                //  about drawing this quad.
                mexDebugPrintf("Nearly collinear twisted edges; not drawing.\n");
                return;
            }
            
            // Solve matrix equation n*p = dp to find intersection point
            d = 1. / det;
            p1 = (+n22*dp1 - n12*dp2) * d;
            p2 = (-n21*dp1 + n11*dp2) * d;
            
            mexDebugPrintf("Twisted quad: intersection point (%f,%f)\n", p1, p2);
            
            // Create new vertex list with intersection point p added.
            T nx1[5] = {x1[0], x1[1], x1[2], x1[3], p1};
            T nx2[5] = {x2[0], x2[1], x2[2], x2[3], p2};
            
            // Render triangles.
            RenderTri(image, nx1, nx2, a1, b0, 4, value, m, n, periodic);
            RenderTri(image, nx1, nx2, a0, b1, 4, value, m, n, periodic);

            break;
    }
}



/*
 * Render an (anti-aliased) quadrilateral and add it to the image
 *
 *  O = RenderQuad(O, x1, x2)
 *  O = RenderQuad(O, x1, x2, v)
 *  O = RenderQuad(O, x1, x2, v, p)
 *
 *      O: image (array)
 * x1, x2: length-4 vectors with the x1/x2 coordinates of the vertices
 *          (in grid space)
 *      v: scalar; value of the quadrilateral (defaults to 1)
 *      p: scalar flag: nonzero for a periodic image
 *          (coordinates wrap around)
 */

void mexFunction(int nlhs, mxArray *plhs[],
        int nrhs, const mxArray *prhs[])
{
    const mxArray *oMX, *x1MX, *x2MX, *vMX, *perMX;
    double v;
    mxArray *outMX;
    int m, n;
    bool periodic;
	
    if (nlhs > 1) mexErrMsgTxt("Too many output arguments.");
    if (nrhs < 3 || nrhs > 5) mexErrMsgTxt("Wrong number of input arguments.");
    
    oMX = prhs[0];
    x1MX = prhs[1];
    x2MX = prhs[2];
    
    if (mxIsComplex(oMX) || mxIsComplex(x1MX) || mxIsComplex(x2MX))
        mexErrMsgTxt("Inputs must be real.");
    
    m = mxGetM(oMX);
    n = mxGetN(oMX);
    
    if (nrhs >= 4) {
        vMX = prhs[3];
        if (!mxIsScalar(vMX) || mxIsComplex(vMX) || !mxIsNumeric(vMX))
            mexErrMsgTxt("v must be a real scalar.");
        
        v = mxGetScalar(vMX);
    } else {
        v = 1.0;
    }
	
	if (nrhs >= 5) {
        perMX = prhs[4];
        if (!mxIsScalar(perMX) || mxIsComplex(perMX) || !mxIsNumeric(perMX))
            mexErrMsgTxt("The periodic flag must be a real scalar.");
        
        periodic = (mxGetScalar(perMX) != 0);
	} else {
		periodic = false;
	}
    
    plhs[0] = outMX = mxDuplicateArray(oMX);
    if (mxIsDouble(oMX) && mxIsDouble(x1MX) && mxIsDouble(x2MX)) {
        RenderQuad((double *) mxGetData(outMX),
                (double *) mxGetData(x1MX),
                (double *) mxGetData(x2MX), v, m, n, periodic);
    } else if (mxIsSingle(oMX) && mxIsSingle(x1MX) && mxIsSingle(x2MX)) {
        RenderQuad((float *) mxGetData(outMX),
                (float *) mxGetData(x1MX),
                (float *) mxGetData(x2MX), (float) v, m, n, periodic);
    } else
        mexErrMsgTxt("Inputs must be all single or all double.");
}
